<style>
    * {
              margin: 0;
              padding: 0;
            }
            
            body {
              background: #C2BEB2;
            }
            
            .center {
              /* position: absolute; */
              position: relative;
              top: 0;
              left: 0;
              right: 0;
              bottom: 10px;
              margin: auto;
            }
            
            .table {
              width: 220px;
              height: 47px;
              background-color: #DECBA4;
              border-radius: 10px;
            }
            .table .monitor-wrapper {
              background: #3E5151;
              width: 217px;
              border-radius: 10px;
              height: 45px;
              box-shadow: 0px 2px 2px 2px rgba(0, 0, 0, 0.3);
            }
            .table .monitor-wrapper .monitor {
              width: 210px;
              height: 40px;
              border-radius: 10px;
              background-color: #34363850;
